import React, { useEffect, useState } from "react";
import { supabase } from "../supabase/client";
import { getCartItems, removeFromCart } from "../supabase/cartService";
import { Link, useNavigate } from "react-router-dom";

const Cart = () => {
  const [items, setItems] = useState([]);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const loadCart = async () => {
      const { data: { user } } = await supabase.auth.getUser();
      setUser(user);

      if (user) {
        const cartItems = await getCartItems(); // ‚úÖ sin par√°metros
        setItems(cartItems);
      }
      setLoading(false);
    };

    loadCart();
  }, []);

  const total = items.reduce(
    (acc, item) => acc + item.products.price * item.quantity,
    0
  );

  const handleRemove = async (itemId) => {
    await removeFromCart(itemId);
    setItems(items.filter((i) => i.id !== itemId));
  };

  const handleCheckout = () => {
    navigate("/checkout", { state: { items, total } });
  };

  if (loading) return <p>Cargando carrito...</p>;

  if (!user)
    return (
      <p style={{ padding: "40px" }}>
        Debes <Link to="/login">iniciar sesi√≥n</Link> para ver tu carrito.
      </p>
    );

  if (items.length === 0)
    return <p style={{ padding: "40px" }}>Tu carrito est√° vac√≠o ü¶é</p>;

  return (
    <div style={{ padding: "40px" }}>
      <h2>üõí Tu carrito</h2>

      {items.map((item) => (
        <div
          key={item.id}
          style={{
            borderBottom: "1px solid #ccc",
            paddingBottom: "10px",
            marginBottom: "10px",
          }}
        >
          <p>
            <strong>{item.products.name}</strong> ‚Äî {item.quantity} √ó $
            {item.products.price}
          </p>
          <button onClick={() => handleRemove(item.id)}>‚ùå Quitar</button>
        </div>
      ))}

      <h3>Total: ${total.toFixed(2)}</h3>
      <button
        onClick={handleCheckout}
        style={{
          marginTop: "10px",
          padding: "10px 20px",
          background: "#4CAF50",
          color: "white",
          border: "none",
          cursor: "pointer",
          borderRadius: "6px",
        }}
      >
        Proceder al pago
      </button>
    </div>
  );
};

export default Cart;
