// src/supabase/cartService.js
import { supabase } from "./client";

/**
 * üîπ Obtiene o crea el carrito del usuario actual.
 */
export async function getOrCreateCart(userId) {
  if (!userId) throw new Error("Usuario no autenticado.");

  // Buscar si ya existe un carrito para este usuario
  const { data: existingCart, error: fetchError } = await supabase
    .from("carts")
    .select("*")
    .eq("user_id", userId)
    .single();

  // Si hubo error distinto a "no encontrado" ‚Üí lanzamos
  if (fetchError && fetchError.code !== "PGRST116") {
    console.error("Error al obtener carrito:", fetchError.message);
    throw fetchError;
  }

  // Si ya existe, lo devolvemos
  if (existingCart) return existingCart;

  // Si no existe, creamos uno nuevo
  const { data: newCart, error: createError } = await supabase
    .from("carts")
    .insert({ user_id: userId })
    .select()
    .single();

  if (createError) {
    console.error("Error al crear carrito:", createError.message);
    throw createError;
  }

  return newCart;
}

/**
 * üîπ Agrega un producto al carrito del usuario actual.
 * Verifica sesi√≥n, stock y cantidad disponible.
 */
export const addToCart = async (productId, quantity = 1) => {
  const { data: { user }, error: userError } = await supabase.auth.getUser();

  if (userError || !user) {
    alert("Debes iniciar sesi√≥n para a√±adir productos al carrito ü¶é");
    return;
  }

  const { data, error } = await supabase
    .from("cart")
    .upsert([
      {
        user_id: user.id,
        product_id: productId,
        quantity,
      },
    ])
    .select();

  if (error) console.error("Error al agregar al carrito:", error);
  return data;


  try {
    const cart = await getOrCreateCart(user.id);

    // Verificar si el producto ya est√° en el carrito
    const { data: existingItem } = await supabase
      .from("cart_items")
      .select("*")
      .eq("cart_id", cart.id)
      .eq("product_id", productId)
      .single();

    // Consultar stock del producto
    const { data: product, error: productError } = await supabase
      .from("products")
      .select("stock")
      .eq("id", productId)
      .single();

    if (productError) throw productError;

    if (!product) {
      alert("‚ö†Ô∏è Producto no encontrado.");
      return;
    }

    if (product.stock <= 0) {
      alert("‚ùå No hay stock disponible de este producto.");
      return;
    }

    // Si ya estaba en el carrito, actualizamos cantidad
    if (existingItem) {
      const newQuantity = Math.min(existingItem.quantity + quantity, product.stock);

      await supabase
        .from("cart_items")
        .update({ quantity: newQuantity })
        .eq("id", existingItem.id);
    } else {
      // Si no estaba, lo insertamos
      await supabase
        .from("cart_items")
        .insert({ cart_id: cart.id, product_id: productId, quantity });
    }

    alert("‚úÖ Producto agregado al carrito.");
  } catch (err) {
    console.error("Error al agregar al carrito:", err.message);
    alert("Ocurri√≥ un error al agregar el producto al carrito.");
  }
}

/**
 * üîπ Obtiene todos los productos del carrito del usuario actual.
 */
export async function getCartItems() {
  const { data: { user } } = await supabase.auth.getUser();
  if (!user) return [];

  try {
    const cart = await getOrCreateCart(user.id);

    const { data, error } = await supabase
      .from("cart_items")
      .select(`
        id,
        quantity,
        products (
          id,
          name,
          price,
          stock,
          image_url
        )
      `)
      .eq("cart_id", cart.id);

    if (error) throw error;

    return data || [];
  } catch (err) {
    console.error("Error al obtener items del carrito:", err.message);
    return [];
  }
}

/**
 * üîπ Elimina un producto espec√≠fico del carrito.
 */
export async function removeFromCart(itemId) {
  const { error } = await supabase.from("cart_items").delete().eq("id", itemId);
  if (error) console.error("Error al eliminar del carrito:", error.message);
}

/**
 * üîπ Vac√≠a completamente el carrito del usuario actual.
 */
export async function clearCart() {
  const { data: { user } } = await supabase.auth.getUser();
  if (!user) return;

  const cart = await getOrCreateCart(user.id);
  const { error } = await supabase.from("cart_items").delete().eq("cart_id", cart.id);

  if (error) console.error("Error al limpiar carrito:", error.message);
}
