import React, { useEffect, useState } from "react";
import { supabase } from "../supabase/Client";
import styles from "./AdminPanel.module.css";

const AdminPanel = () => {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    name: "",
    description: "",
    price: "",
    stock: "",
    image_url: "",
    category_id: "",
  });

  // ðŸ”¹ Obtener productos
  useEffect(() => {
    const fetchProducts = async () => {
      const { data, error } = await supabase.from("products").select("*");
      if (error) console.error("Error al cargar productos:", error);
      else setProducts(data);
    };
    fetchProducts();
  }, []);

  // ðŸ”¹ Manejar cambios en los inputs
  const handleChange = (e) => {
    setNewProduct({ ...newProduct, [e.target.name]: e.target.value });
  };

  // ðŸ”¹ Agregar producto
  const handleAddProduct = async (e) => {
    e.preventDefault();
    const { data, error } = await supabase.from("products").insert([newProduct]);
    if (error) alert("Error al agregar producto: " + error.message);
    else {
      alert("Producto agregado correctamente!");
      setProducts([...products, ...data]);
      setNewProduct({
        name: "",
        description: "",
        price: "",
        stock: "",
        image_url: "",
        category_id: "",
      });
    }
  };

  // ðŸ”¹ Eliminar producto
  const handleDelete = async (id) => {
    const { error } = await supabase.from("products").delete().eq("id", id);
    if (error) alert("Error al eliminar: " + error.message);
    else setProducts(products.filter((p) => p.id !== id));
  };

  return (
    <div className={styles.adminContainer}>
      <h2>Panel del Administrador ðŸ¦Ž</h2>

      <form onSubmit={handleAddProduct} className={styles.form}>
        <input
          type="text"
          name="name"
          placeholder="Nombre"
          value={newProduct.name}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="description"
          placeholder="DescripciÃ³n"
          value={newProduct.description}
          onChange={handleChange}
        />
        <input
          type="number"
          name="price"
          placeholder="Precio"
          value={newProduct.price}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="stock"
          placeholder="Stock"
          value={newProduct.stock}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="image_url"
          placeholder="URL de imagen"
          value={newProduct.image_url}
          onChange={handleChange}
        />
        <input
          type="number"
          name="category_id"
          placeholder="ID de categorÃ­a"
          value={newProduct.category_id}
          onChange={handleChange}
        />

        <button type="submit">Agregar producto</button>
      </form>

      <div className={styles.productList}>
        {products.map((p) => (
          <div key={p.id} className={styles.card}>
            <img src={p.image_url} alt={p.name} />
            <h3>{p.name}</h3>
            <p>{p.description}</p>
            <p>${p.price}</p>
            <p>Stock: {p.stock}</p>
            <button onClick={() => handleDelete(p.id)}>Eliminar</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AdminPanel;
