import React, { useEffect, useState } from "react";
import { supabase } from "../supabase/client";
import styles from "./OrdersAdmin.module.css";

const OrdersAdmin = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState(null);

  // 🔐 Solo el admin puede ver esta página
  const ADMIN_EMAIL = "admin@tiendareptil.com"; // ⚠️ Asegúrate de que este sea tu correo de administrador

  useEffect(() => {
    const fetchOrders = async () => {
      const { data: userData } = await supabase.auth.getUser();
      const currentUser = userData?.user;
      setUser(currentUser);

      if (!currentUser || currentUser.email !== ADMIN_EMAIL) {
        setLoading(false);
        return;
      }

      // CONSULTA SIMPLIFICADA: Solo trae los campos de 'orders' (id, total, status, created_at, user_id)
      // Esto aísla el problema a la tabla 'orders' o a su RLS.
      const { data, error } = await supabase
        .from("orders")
        .select(`
          id,
          total,
          status,
          created_at,
          user_id
          /*
          // Omitimos las uniones por ahora para el diagnóstico:
          profiles ( name, address ),
          order_items (
            quantity,
            price,
            products ( name )
          )
          */
        `)
        .order("created_at", { ascending: false });

      if (error) {
        console.error("Error al cargar pedidos:", error);
      } else {
        setOrders(data);
      }
      setLoading(false);
    };

    fetchOrders();
  }, []);

  const updateStatus = async (orderId, newStatus) => {
    // ⚠️ Importante: Los botones de acción no están en el render actual, 
    // pero la función está aquí para cuando los reincorpores.
    const { error } = await supabase
      .from("orders")
      .update({ status: newStatus })
      .eq("id", orderId);

    if (!error) {
      setOrders((prev) =>
        prev.map((order) =>
          order.id === orderId ? { ...order, status: newStatus } : order
        )
      );
    }
  };

  if (loading) return <p>Cargando pedidos...</p>;

  if (!user || user.email !== ADMIN_EMAIL)
    return <p>❌ No tienes permiso para ver esta página.</p>;

  return (
    <div className={styles.container}>
      <h2>📦 Panel de Pedidos (Diagnóstico RLS)</h2>
      {orders.length === 0 ? (
        <p>No hay pedidos registrados.</p>
      ) : (
        <div className={styles.ordersList}>
          {orders.map((order) => (
            <div key={order.id} className={styles.orderCard}>
              <h3>Pedido #{order.id}</h3>
              <p>
                <strong>ID de Usuario:</strong> {order.user_id}
              </p>
              <p>
                <strong>Fecha:</strong> {new Date(order.created_at).toLocaleString()}
              </p>
              <p>
                <strong>Total:</strong> ${order.total}
              </p>
              <p>
                <strong>Estado:</strong>{" "}
                <span className={styles.status}>{order.status}</span>
              </p>
              {/* No mostramos los productos ni la dirección ya que no los estamos consultando */}
              <div className={styles.actions}>
                <button onClick={() => updateStatus(order.id, "pagado")}>
                  💳 Marcar como Pagado
                </button>
                <button onClick={() => updateStatus(order.id, "enviado")}>
                  🚚 Marcar como Enviado
                </button>
                <button onClick={() => updateStatus(order.id, "completado")}>
                  ✅ Completar
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default OrdersAdmin;
