import React, { useEffect, useState } from "react";
import { supabase } from "../supabase/client";
import { getCartItems, removeFromCart } from "../supabase/cartService";
import { Link, useNavigate } from "react-router-dom";

const Cart = () => {
  const [items, setItems] = useState([]);
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    supabase.auth.getUser().then(async ({ data }) => {
      const u = data?.user;
      setUser(u);
      if (u) {
        const cartItems = await getCartItems(u.id);
        setItems(cartItems);
      }
    });
  }, []);

  const total = items.reduce(
    (acc, item) => acc + item.products.price * item.quantity,
    0
  );

  const handleRemove = async (itemId) => {
    await removeFromCart(itemId);
    const updated = items.filter((i) => i.id !== itemId);
    setItems(updated);
  };

  const handleCheckout = () => {
    navigate("/checkout", { state: { items, total } });
  };

  if (!user)
    return (
      <p>
        Debes <Link to="/login">iniciar sesi√≥n</Link> para ver tu carrito.
      </p>
    );

  if (items.length === 0) return <p>Tu carrito est√° vac√≠o ü¶é</p>;

  return (
    <div style={{ padding: "40px" }}>
      <h2>üõí Tu carrito</h2>
      {items.map((item) => (
        <div key={item.id}>
          <p>
            {item.products.name} ‚Äî {item.quantity} √ó ${item.products.price}
          </p>
          <button onClick={() => handleRemove(item.id)}>‚ùå Quitar</button>
        </div>
      ))}
      <h3>Total: ${total.toFixed(2)}</h3>
      <button onClick={handleCheckout}>Proceder al pago</button>
    </div>
  );
};

export default Cart;
