import { useEffect, useState } from "react";
import { supabase } from "../supabase/client";
import ProductCard from "../components/ProductCard";
import styles from "./Home.module.css"; 

function Home() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    getProducts();
  }, []);

  async function getProducts() {
    // Usamos la tabla "products" para consistencia
    const { data, error } = await supabase.from("products").select("*").eq("is_active", true);
    if (error) console.error("Error cargando productos:", error);
    else setProducts(data);
  }

  // üö® FUNCI√ìN DE SCROLL CORREGIDA Y SIMPLIFICADA
  const scrollToCatalog = () => {
    const catalogSection = document.getElementById('catalog-start');
    if (catalogSection) {
      catalogSection.scrollIntoView({ behavior: 'smooth' });
    } else {
      console.warn("Elemento 'catalog-start' no encontrado para el scroll.");
    }
  };

  return (
    <div style={{ padding: "0 2rem" }}>
      
      {/* 1. SECCI√ìN PRINCIPAL (HERO) */}
      <div className={styles.heroContainer}>
        <h1 className={styles.heroTitle}>
          EL REINO ESCAMADO üêç
        </h1>
        <p className={styles.heroSubtitle}>
          Encuentra a tu nuevo compa√±ero ex√≥tico. Expertos en iguanas, geckos y serpientes de la m√°s alta calidad y cuidado.
        </p>
        <button 
          className={styles.heroButton}
          onClick={scrollToCatalog} // üö® Usamos la funci√≥n aqu√≠
        >
          Ver Cat√°logo Completo
        </button>
      </div>

      {/* 2. MENSAJES CLAVE (FEATURES) */}
      <div className={styles.featuresContainer}>
        <div className={styles.featureItem}>
          <div className={styles.featureIcon}>‚úÖ</div>
          <h4>Garant√≠a de Salud</h4>
          <p>Todos nuestros reptiles pasan por chequeos veterinarios rigurosos.</p>
        </div>
        <div className={styles.featureItem}>
          <div className={styles.featureIcon}>üöö</div>
          <h4>Env√≠o Seguro</h4>
          <p>Transporte especializado para garantizar la llegada segura de tu mascota.</p>
        </div>
        <div className={styles.featureItem}>
          <div className={styles.featureIcon}>üìû</div>
          <h4>Soporte Experto</h4>
          <p>Asesor√≠a gratuita de por vida para el cuidado de tu nuevo reptil.</p>
        </div>
      </div>

      <hr style={{ borderTop: '1px solid #ccc', margin: '4rem 0 3rem' }} />

      {/* 3. CAT√ÅLOGO DE PRODUCTOS */}
      {/* üö® AQU√ç EST√Å EL ID CRUCIAL */}
      <h2 className={styles.catalogTitle} id="catalog-start">
        ü¶é NUESTRO CAT√ÅLOGO DE REPTILES
      </h2>
      <div className={styles.productsGrid}>
        {products.map((p) => (
          <ProductCard key={p.id} product={p} />
        ))}
      </div>
    </div>
  );
}

export default Home;
